    def modifier_offre(self):
        id_offre = self.lineEditID.text()
        connection, cursor = connect_to_DB()
        nom = self.lineEditNom.text()
        prix = self.lineEditDatePrix.text()
        sport = self.dropdownSport.currentText()
        query = "UPDATE offre SET NOM_offre = %s, sport = %s, prix = %s WHERE ID_offre = %s"
        values = (nom,sport,int(prix), id_offre)
        cursor.execute(query, values)
        connection.commit()
        self.msg = QtWidgets.QMessageBox()
        self.msg.setIcon(QtWidgets.QMessageBox.Information)
        self.msg.setText("La modification a terminé avec succès")
        self.msg.setWindowTitle("Success")
        self.msg.exec_()
        self.main_window.update_object_list()
